import React,{useState} from 'react';
import './App.css';
import './lit.css'

const  brightness= isLit? 'lit':'dark';



function Room() {

  const [isLit,setLit]=useState(true);


  return (

    <div className={'room ${brightness}'} >
      this room is lit{isLit? 'lit' : 'dark'}<br />
    <button onClick={()=>setLit(!isLit)}>Flip</button>

    </div>



  );
}

export default Room;
